mapper.select:
SELECT *
FROM systemcode


        code_index = #{codeIndex}


        AND code_value = #{codeValue}


        AND code_name LIKE CONCAT('%', #{codeName}, '%')

SELECT COUNT(*)
FROM systemcode
WHERE code_value = #{codeValue} AND code_name = #{codeName}

SELECT EXISTS(
    SELECT 1
    FROM systemcode
    WHERE code_value = #{codeValue}
)

SELECT COUNT(*)
FROM systemcode
WHERE code_name = #{codeName}

SELECT EXISTS(
    SELECT 1
    FROM systemcode
    WHERE code_value = #{codeValue}
)

SELECT EXISTS(
    SELECT 1
    FROM systemcode
    WHERE code_name = #{codeName}
)

SELECT EXISTS(
    SELECT 1
    FROM systemcode
    WHERE code_value = #{codeValue} AND code_id != #{codeId}
)

SELECT EXISTS(
    SELECT 1
    FROM systemcode
    WHERE code_name = #{codeName} AND code_id != #{codeId}
)
