mapper.update:
UPDATE systemcode
SET code_value = #{codeValue}, code_name = #{codeName}, modified_by = #{modifiedBy}
WHERE code_id = #{codeId}

mapper.select:
SELECT *
FROM systemcode


        code_index = #{codeIndex}


        AND code_value = #{codeValue}


        AND code_name LIKE CONCAT('%', #{codeName}, '%')

SELECT COUNT(*)
FROM systemcode
WHERE code_value = #{codeValue} AND code_name = #{codeName}

SELECT COUNT(*)
FROM systemcode
WHERE code_value = #{codeValue}

SELECT COUNT(*)
FROM systemcode
WHERE code_name = #{codeName}
