mapper.insert:
INSERT INTO users (
    login_id, password, username, email, phone_number, branch, role, join_date
) VALUES (
             #{loginId}, #{password}, #{username}, #{email}, #{phoneNumber}, #{branch}, #{role}, #{joinDate}
         )

mapper.delete:
DELETE FROM users
WHERE user_id IN

    #{id}

mapper.update:
UPDATE users
SET account_locked = TRUE
WHERE login_id = #{loginId}

UPDATE users
SET login_fail_count = 0
WHERE login_id = #{loginId}
